class OptimizedLZW {
    constructor() {
        this.reset();
    }

    reset() {
        this.dictionary = {};
        this.nextCode = 256;
        this.initDictionary();
    }

    initDictionary() {
        // Базовый словарь (0-255 для стандартных ASCII)
        for (let i = 0; i < 256; i++) {
            this.dictionary[String.fromCharCode(i)] = i;
        }
    }

    compress(input) {
        this.reset();
        let current = "";
        const output = [];

        for (const char of input) {
            const combined = current + char;

            if (this.dictionary.hasOwnProperty(combined)) {
                current = combined;
            } else {
                // Добавляем текущую последовательность в выход
                output.push(this.getCode(current));

                // Добавляем новую комбинацию в словарь
                this.dictionary[combined] = this.nextCode++;

                // Начинаем новую последовательность с текущего символа
                current = char;
            }
        }

        // Добавляем последнюю последовательность
        if (current) {
            output.push(this.getCode(current));
        }

        return output;
    }

    decompress(compressed) {
        this.reset();
        const dict = {};

        // Обратный словарь (код -> строка)
        for (const key in this.dictionary) {
            dict[this.dictionary[key]] = key;
        }
        console.log("Обратный словарь (код -> строка)");
        let prevEntry = dict[compressed[0]];
        let result = prevEntry;

        for (let i = 1; i < compressed.length; i++) {
            const code = compressed[i];
            let entry;

            if (dict[code]) {
                entry = dict[code];
            } else {
                // Специальный случай для последовательностей вида cScSc
                entry = prevEntry + prevEntry[0];
            }

            result += entry;

            // Добавляем новую комбинацию в словарь
            dict[this.nextCode++] = prevEntry + entry[0];

            prevEntry = entry;
        }
        console.log(" строка)");

        return result;
    }

    getCode(str) {
        if (str.length === 1) {
            const charCode = str.charCodeAt(0);

            // Оптимизация для a-z (97-122 в ASCII)
            if (charCode >= 97 && charCode <= 122) {
                // Используем только 5 бит (0-25) для a-z
                return charCode - 97;
            }

            return charCode;
        }

        // Для строк длиной >1 используем стандартные коды (начиная с 256)
        return this.dictionary[str];
    }
}

const lzw = new OptimizedLZW();

// Пример сжатия и распаковки
const text =
    "";
const compressed = lzw.compress(text);
console.log("Compressed:", compressed);

const decompressed = lzw.decompress(compressed);
console.log("Decompressed:", decompressed);
console.log("Match:", text === decompressed);

// Размер в битах (примерно)
const originalSize = text.length * 8;
const compressedSize = compressed.reduce((sum, code) => {
    return sum + (code < 26 ? 5 : code < 256 ? 8 : 12); // 12 бит для кодов >255
}, 0);

console.log(`Original: ${originalSize} bits, Compressed: ${compressedSize} bits`);
console.log(`Compression ratio: ${((compressedSize / originalSize) * 100).toFixed(1)}%`);
