class Huffman {
    static buildFrequencyTable(data) {
        const freq = {};
        for (const char of data) {
            freq[char] = (freq[char] || 0) + 1;
        }
        return freq;
    }

    static buildHuffmanCodes(freq) {
        const nodes = Object.entries(freq).map(([char, count]) => ({
            char,
            count,
        }));

        while (nodes.length > 1) {
            nodes.sort((a, b) => a.count - b.count);
            const left = nodes.shift();
            const right = nodes.shift();
            nodes.push({
                char: null,
                count: left.count + right.count,
                left,
                right,
            });
        }

        const codes = {};
        const traverse = (node, code) => {
            if (node.char) {
                codes[node.char] = code || "0"; // Если всего 1 символ, код '0'
                return;
            }
            traverse(node.left, code + "0");
            traverse(node.right, code + "1");
        };

        traverse(nodes[0], "");
        return codes;
    }

    static encode(data, codes) {
        return data.map((char) => codes[char]).join("");
    }

    static decode(encodedBits, codes) {
        const reverseCodes = {};
        for (const [char, code] of Object.entries(codes)) {
            reverseCodes[code] = char;
        }

        let decoded = [];
        let currentBits = "";
        for (const bit of encodedBits) {
            currentBits += bit;
            if (reverseCodes[currentBits]) {
                decoded.push(reverseCodes[currentBits]);
                currentBits = "";
            }
        }
        return decoded;
    }
}

// Комбинированный LZW + Хаффман
class LZWWithHuffman {
    constructor() {
        this.reset();
    }

    reset() {
        this.dictionary = {};
        for (let i = 0; i < 256; i++) {
            this.dictionary[String.fromCharCode(i)] = i;
        }
        this.nextCode = 256;
    }

    encode(input) {
        this.reset();
        let outputCodes = [];
        let newSymbols = []; // Здесь храним новые символы для Хаффмана

        let currentPhrase = "";
        for (const char of input) {
            const newPhrase = currentPhrase + char;
            if (newPhrase in this.dictionary) {
                currentPhrase = newPhrase;
            } else {
                outputCodes.push(this.dictionary[currentPhrase]);
                newSymbols.push(char); // Запоминаем новый символ
                this.dictionary[newPhrase] = this.nextCode++;
                currentPhrase = char;
            }
        }

        if (currentPhrase) {
            outputCodes.push(this.dictionary[currentPhrase]);
        }

        // Сжимаем новые символы через Хаффмана
        const freqTable = Huffman.buildFrequencyTable(newSymbols);
        const huffmanCodes = Huffman.buildHuffmanCodes(freqTable);
        const compressedNewSymbols = Huffman.encode(newSymbols, huffmanCodes);

        return {
            lzwCodes: outputCodes, // Обычные LZW-коды
            huffmanCodes, // Таблица кодов Хаффмана
            compressedNewSymbols, // Новые символы в битах
        };
    }

    decode(encoded) {
        this.reset();
        const { lzwCodes, huffmanCodes, compressedNewSymbols } = encoded;

        // Восстанавливаем новые символы из битов Хаффмана
        const newSymbols = Huffman.decode(compressedNewSymbols.split(""), huffmanCodes);

        // Восстанавливаем словарь LZW
        const dictionary = {};
        for (let i = 0; i < 256; i++) {
            dictionary[i] = String.fromCharCode(i);
        }
        let nextCode = 256;

        let output = "";
        let prevEntry = dictionary[lzwCodes[0]];
        output += prevEntry;

        let symbolIndex = 0;
        for (let i = 1; i < lzwCodes.length; i++) {
            const code = lzwCodes[i];
            let entry;

            if (code in dictionary) {
                entry = dictionary[code];
            } else if (code === nextCode) {
                // Особый случай (например, "abababa")
                entry = prevEntry + newSymbols[symbolIndex++];
            } else {
                throw new Error("Неверный код LZW: " + code);
            }

            output += entry;
            dictionary[nextCode++] = prevEntry + entry[0];
            prevEntry = entry;
        }

        return output;
    }
}

// Пример использования
const lzwHuff = new LZWWithHuffman();
// const input = "tobeornottobeortobeornot"; // Пример с повторениями
const input =
    "";
const encoded = lzwHuff.encode(input);
// console.log("LZW-коды:", encoded.lzwCodes.length);
console.log("LZW-коды:", encoded.lzwCodes);
// console.log("Коды Хаффмана:");
console.log("Коды Хаффмана:", encoded.huffmanCodes);
// console.log("Сжатые новые символы:");
console.log("Сжатые новые символы:", encoded.compressedNewSymbols);

const decoded = lzwHuff.decode(encoded);
console.log("Декодировано:");
// console.log("Декодировано:", decoded);
console.log("Совпадает с исходным?", input === decoded); // true
