function buildSuffixArray(s) {
    const n = s.length;
    const sa = Array(n)
        .fill(0)
        .map((_, i) => i);
    sa.sort((a, b) => s.localeCompare(s.slice(b) + s.slice(0, b)));
    return sa;
}

function bwt(input) {
    const endMarker = "\x00"; // Специальный символ конца
    const s = input + endMarker;
    const sa = buildSuffixArray(s);
    let transformed = "";
    let originalIdx = 0;

    for (let i = 0; i < sa.length; i++) {
        const pos = sa[i];
        transformed += pos === 0 ? s[s.length - 1] : s[pos - 1];
        if (pos === 0) originalIdx = i;
    }

    return { bwt: transformed, index: originalIdx };
}

function rleEncode(data) {
    let encoded = "";
    let count = 1;
    for (let i = 1; i <= data.length; i++) {
        if (i < data.length && data[i] === data[i - 1]) {
            count++;
        } else {
            encoded += data[i - 1] + (count > 1 ? count.toString() : "");
            count = 1;
        }
    }
    return encoded;
}

class LZW {
    static encode(input) {
        let dict = {};
        for (let i = 0; i < 256; i++) dict[String.fromCharCode(i)] = i;
        let nextCode = 256;
        let output = [];
        let current = "";

        for (const char of input) {
            const combined = current + char;
            if (combined in dict) {
                current = combined;
            } else {
                output.push(dict[current]);
                dict[combined] = nextCode++;
                current = char;
            }
        }
        if (current) output.push(dict[current]);
        return output;
    }

    static decode(encodedData) {
        // Инициализация словаря (обратный процесс к encode)
        let dict = {};
        for (let i = 0; i < 256; i++) {
            dict[i] = String.fromCharCode(i);
        }
        let nextCode = 256;
        let currentStr = String.fromCharCode(encodedData[0]);
        let result = currentStr;

        for (let i = 1; i < encodedData.length; i++) {
            const code = encodedData[i];
            let entry;

            if (dict[code] !== undefined) {
                entry = dict[code];
            } else if (code === nextCode) {
                // Специальный случай для повторяющихся паттернов (например, "ABABABA")
                entry = currentStr + currentStr[0];
            } else {
                throw new Error(`Некорректный код: ${code}`);
            }

            result += entry;

            // Добавляем новую последовательность в словарь
            dict[nextCode++] = currentStr + entry[0];
            currentStr = entry;
        }

        return result;
    }
}

function buildHuffmanCodes(freq) {
    const nodes = Object.entries(freq).map(([char, count]) => ({
        char,
        count,
    }));

    while (nodes.length > 1) {
        nodes.sort((a, b) => a.count - b.count);
        const left = nodes.shift();
        const right = nodes.shift();
        nodes.push({
            char: null,
            count: left.count + right.count,
            left,
            right,
        });
    }

    const codes = {};
    const traverse = (node, code) => {
        if (node.char) {
            codes[node.char] = code || "0"; // Если всего 1 символ, код '0'
            return;
        }
        traverse(node.left, code + "0");
        traverse(node.right, code + "1");
    };

    traverse(nodes[0], "");
    return codes;
}

function compress(data) {
    // 1. Применяем BWT
    const { bwt: transformedBWT, index } = bwt(data);

    // 2. Выбираем RLE или LZW
    const rleEncoded = rleEncode(transformedBWT);
    // ИЛИ
    const lzwEncoded = LZW.encode(transformedBWT);

    // 3. Опционально: Хаффман
    const huffmanEncoded = buildHuffmanCodes(lzwEncoded.join(","));

    return {
        bwtIndex: index,
        compressed: huffmanEncoded, // или rleEncoded
        method: "LZW+Huffman", // или 'RLE'
    };
}

function ibwt(transformed, index) {
    const n = transformed.length;
    let table = Array(n).fill("");

    // Постепенно восстанавливаем таблицу
    for (let i = 0; i < n; i++) {
        for (let j = 0; j < n; j++) {
            table[j] = transformed[j] + table[j];
        }
        table.sort();
    }

    // Возвращаем строку без маркера
    return table[index].replace("$", "");
}
function rleDecode(encoded) {
    let decoded = "";
    let i = 0;
    while (i < encoded.length) {
        const char = encoded[i++];
        let countStr = "";
        // Считываем цифры после символа
        while (i < encoded.length && /\d/.test(encoded[i])) {
            countStr += encoded[i++];
        }
        const count = countStr ? parseInt(countStr, 10) : 1;
        decoded += char.repeat(count);
    }
    return decoded;
}

function huffmanDecode(bitString, huffmanCodes) {
    // Инвертируем таблицу кодов: { "A": "0" } → { "0": "A" }
    const reverseCodes = {};
    for (const [char, code] of Object.entries(huffmanCodes)) {
        reverseCodes[code] = char;
    }

    let decoded = "";
    let currentBits = "";
    for (const bit of bitString) {
        currentBits += bit;
        if (reverseCodes[currentBits]) {
            decoded += reverseCodes[currentBits];
            currentBits = "";
        }
    }
    return decoded;
}

function decompress(compressedData) {
    let decodedData;

    // 1. Декодируем Хаффман (если использовали)
    if (compressedData.huffmanCodes) {
        decodedData = huffmanDecode(compressedData.compressedBits, compressedData.huffmanCodes);
    } else {
        decodedData = compressedData.compressedData;
    }

    // 2. Декодируем RLE или LZW
    let bwtTransformed;
    if (compressedData.method === "RLE") {
        bwtTransformed = rleDecode(decodedData);
    } else {
        const lzwCodes = decodedData.split(",").map(Number);
        bwtTransformed = LZW.decode(lzwCodes);
    }

    // 3. Обратное BWT
    return ibwt(bwtTransformed, compressedData.bwtIndex);
}

