class TrieNode {
    constructor() {
        this.children = {}; // { char → TrieNode }
        this.code = null; // числовой код (если терминальный)
        this.useCount = 0; // сколько раз узел был использован
    }
}

class TrieWithCache {
    constructor(maxNodes = 1000) {
        this.root = new TrieNode();
        this.nextCode = 1; // начинаем с 1 (0 можно зарезервировать)
        this.maxNodes = maxNodes; // лимит узлов
        this.currentNodes = 0; // текущее количество узлов
        this.lruCache = new Map(); // LRU-кэш (key: code, value: lastUsedTime)
        this.timeCounter = 0; // для отслеживания времени доступа
    }

    // Добавляет строку в TRIE (возвращает код)
    insert(str) {
        let node = this.root;
        for (const char of str) {
            if (!node.children[char]) {
                node.children[char] = new TrieNode();
                this.currentNodes++;
                this._checkAndCleanCache(); // Проверяем, не нужно ли почистить кэш
            }
            node = node.children[char];
            node.useCount++;
            this.lruCache.set(node.code, this.timeCounter++); // Обновляем LRU
        }

        if (node.code === null) {
            node.code = this.nextCode++;
            return node.code;
        }
        return node.code;
    }

    // Ищет строку в TRIE (возвращает код или undefined)
    find(str) {
        let node = this.root;
        for (const char of str) {
            if (!node.children[char]) return undefined;
            node = node.children[char];
        }
        if (node.code !== null) {
            this.lruCache.set(node.code, this.timeCounter++); // Обновляем LRU
            node.useCount++;
        }
        return node.code;
    }

    // Удаляет редко используемые узлы, если достигнут лимит
    _checkAndCleanCache() {
        if (this.currentNodes <= this.maxNodes) return;

        // Собираем все коды из LRU и сортируем по частоте использования
        const nodesToCheck = [];
        for (const [code, lastUsed] of this.lruCache) {
            nodesToCheck.push({ code, lastUsed });
        }

        // Сортируем по времени последнего использования (чем старее — тем хуже)
        nodesToCheck.sort((a, b) => a.lastUsed - b.lastUsed);

        // Удаляем самые старые узлы (например, 10% от maxNodes)
        const nodesToRemove = Math.floor(this.maxNodes * 0.1);
        for (let i = 0; i < nodesToRemove; i++) {
            const { code } = nodesToCheck[i];
            this._removeNode(code);
        }
    }

    // Удаляет узел с заданным кодом (рекурсивно)
    _removeNode(code, node = this.root, path = []) {
        if (node.code === code) {
            // Нашли узел для удаления
            node.code = null;
            if (Object.keys(node.children).length === 0) {
                // Если нет детей — можно удалить
                let parent = this.root;
                for (let i = 0; i < path.length - 1; i++) {
                    parent = parent.children[path[i]];
                }
                delete parent.children[path[path.length - 1]];
                this.currentNodes--;
                this.lruCache.delete(code);
            }
            return true;
        }

        for (const [char, child] of Object.entries(node.children)) {
            if (this._removeNode(code, child, [...path, char])) {
                return true;
            }
        }

        return false;
    }
}

function lzwEncodeWithCache(str, maxNodes = 1000) {
    const trie = new TrieWithCache(maxNodes);
    // Инициализация символами a-z
    for (let i = 0; i < 26; i++) {
        const char = String.fromCharCode(97 + i);
        trie.insert(char);
    }

    let current = "";
    const result = [];

    for (const char of str) {
        const combined = current + char;
        const code = trie.find(combined);
        if (code !== undefined) {
            current = combined;
        } else {
            result.push(trie.find(current));
            trie.insert(combined);
            current = char;
        }
    }

    if (current !== "") {
        result.push(trie.find(current));
    }

    return result;
}

function lzwDecode(encoded) {
    const dictionary = {};
    for (let i = 0; i < 26; i++) {
        const char = String.fromCharCode(97 + i);
        dictionary[i + 1] = char;
    }

    let nextCode = 27;
    let previous = dictionary[encoded[0]];
    let result = previous;

    for (let i = 1; i < encoded.length; i++) {
        const code = encoded[i];
        let current;

        if (dictionary[code]) {
            current = dictionary[code];
        } else {
            current = previous + previous[0];
        }

        result += current;
        dictionary[nextCode++] = previous + current[0];
        previous = current;
    }

    return result;
}

const original =
    "";
const encoded = lzwEncodeWithCache(original, 1024); // Лимит в 50 узлов
// console.log("Original:", original);
console.log("Encoded:", encoded);
const decoded = lzwDecode(encoded);

console.log("Decoded:", decoded);
console.log("Is correct?", original === decoded); // true
