class FrequencyBinaryTrieNode {
    constructor() {
        this.children = {};
        this.isEnd = false;
        this.code = -1; // Код для LZW
        this.frequency = 0; // Счетчик использования
    }
}
class AdaptiveLZW {
    constructor() {
        this.trie = new FrequencyBinaryTrieNode();
        this.nextCode = 123;
        this.inputSize = 0; // Размер входных данных в байтах
        this.outputSize = 0; // Размер выходных данных в кодах
        this._initDictionary();
    }

    _initDictionary() {
        // Заполняем Trie ASCII-символами
        for (let i = 97; i < 123; i++) {
            const bits = i.toString(2).padStart(8, "0");
            let node = this.trie;
            for (const bit of bits) {
                if (!node.children[bit]) {
                    node.children[bit] = new FrequencyBinaryTrieNode();
                }
                node = node.children[bit];
            }
            node.isEnd = true;
            node.code = i;
            node.frequency = 1; // Минимальная частота
        }
    }

    compress(input) {
        let current = "";
        const output = [];

        for (const char of input) {
            this.inputSize++;
            const combined = current + char;
            const combinedBits = this._toBitString(combined);
            const node = this._searchNode(combinedBits);

            if (node?.isEnd) {
                current = combined;
                node.frequency++;
            } else {
                // Добавляем текущую последовательность в вывод
                const code = this._getCode(current);
                output.push(code);
                this.outputSize++;

                // Проверяем эффективность сжатия
                // if (this._getCompressionRatio() < 2.0) {
                //     this._adaptivePrune();
                // }

                // Добавляем новую комбинацию
                this._insertNode(combinedBits);
                current = char;
            }
        }

        if (current) {
            output.push(this._getCode(current));
            this.outputSize++;
        }

        console.log(this.inputSize);
        console.log(this.outputSize);
        return output;
    }

    decompress(compressedCodes) {
        // Инициализируем словарь как в compress
        // this.resetDictionary();
        const dictionary = {};
        // for (let i = 97; i < 123; i++) {
        //     dictionary[i] = String.fromCharCode(i);
        // }
        let nextCode = 123;

        let result = "";
        let prev = dictionary[compressedCodes[0]];
        result += prev;

        for (let i = 1; i < compressedCodes.length; i++) {
            const code = compressedCodes[i];
            let current;

            if (dictionary[code]) {
                current = dictionary[code];
            } else if (code === nextCode) {
                // Специальный случай для LZW
                current = prev + prev[0];
            } else {
                throw new Error("Некорректный код сжатия");
            }

            result += current;

            // Добавляем новую комбинацию в словарь
            if (prev + current[0] in dictionary === false && nextCode < 4096) {
                dictionary[nextCode] = prev + current[0];
                nextCode++;
            }

            prev = current;
        }

        return result;
    }

    _getCompressionRatio() {
        if (this.outputSize === 0) return Infinity;
        return this.inputSize / this.outputSize;
    }

    _collectAllNodes(node, nodesArray) {
        if (node.isEnd) {
            nodesArray.push(node); // Добавляем только узлы, являющиеся концами слов
        }

        // Рекурсивно обходим все дочерние узлы
        for (const bit in node.children) {
            this._collectAllNodes(node.children[bit], nodesArray);
        }
    }

    _adaptivePrune() {
        const ALL_NODES = [];
        this._collectAllNodes(this.trie, ALL_NODES);

        // Фильтруем только используемые узлы (не ASCII)
        const dictNodes = ALL_NODES.filter((n) => n.code >= 26);

        // Сортируем по частоте использования
        dictNodes.sort((a, b) => a.frequency - b.frequency);

        // Удаляем 20% самых редких
        const toRemove = Math.ceil(dictNodes.length * 0.2);
        for (let i = 0; i < toRemove; i++) {
            dictNodes[i].isEnd = false;
            dictNodes[i].code = -1;
        }

        // Сбрасываем счетчики для более честной оценки
        ALL_NODES.forEach((n) => (n.frequency = Math.floor(n.frequency * 0.8)));
    }

    _searchNode(bitStr) {
        let node = this.trie;
        for (const bit of bitStr) {
            if (!node.children[bit]) return null;
            node = node.children[bit];
        }
        return node;
    }

    _insertNode(bitStr) {
        let node = this.trie;
        for (const bit of bitStr) {
            if (!node.children[bit]) {
                node.children[bit] = new FrequencyBinaryTrieNode();
            }
            node = node.children[bit];
        }
        node.isEnd = true;
        node.code = this.nextCode++;
        node.frequency = 1; // Начальная частота
    }

    _getCode(str) {
        if (str.length === 1) return str.charCodeAt(0);
        const bits = this._toBitString(str);
        const node = this._searchNode(bits);
        return node ? node.code : -1;
    }

    _toBitString(str) {
        return Array.from(str)
            .map((c) => c.charCodeAt(0).toString(2).padStart(8, "0"))
            .join("");
    }

    _pruneTrie() {
        // Находим узлы с наименьшей частотой
        const nodes = [];
        this._collectNodes(this.trie, nodes);

        // Сортируем по частоте и удаляем 10% самых редких
        nodes.sort((a, b) => a.frequency - b.frequency);
        const toRemove = Math.floor(nodes.length * 0.1);
        for (let i = 0; i < toRemove; i++) {
            const node = nodes[i];
            node.isEnd = false; // Помечаем как неиспользуемый
            node.code = -1;
        }
        this.currentSize -= toRemove;
    }

    _collectNodes(node, arr) {
        if (node.isEnd) {
            arr.push(node);
        }
        for (const key in node.children) {
            this._collectNodes(node.children[key], arr);
        }
    }
}

const optimizer = new AdaptiveLZW();
const result = optimizer.compress(
    ""
);
console.log(result.length);

const deoptimizer = new AdaptiveLZW();

const de = deoptimizer.decompress(result);
